from tkinter import *from tkinter import ttkdef tableCount():    x = entry.get()    labelTextRed.set(x + " x 1 = " + str(int(x) * 1))    labelTextBlue.set(x + " x 2 = " + str(int(x) * 2))    labelTextGreen.set(x + " x 3 = " + str(int(x) * 3))    labelTextYellow.set(x + " x 4 = " + str(int(x) * 4))    labelTextPink.set(x + " x 5 = " + str(int(x) * 5))    labelTextPurple.set(x + " x 6 = " + str(int(x) * 6))    labelTextGray.set(x + " x 7 = " + str(int(x) * 7))    labelTextOrange.set(x + " x 8 = " + str(int(x) * 8))    labelTextBlack.set(x + " x 9 = " + str(int(x) * 9))    labelTextBrown.set(x + " x 10 = " + str(int(x) * 10))def tableFive():    labelTextRed.set("5 x 1 = 5")    labelTextBlue.set("5 x 2 = 10")    labelTextGreen.set("5 x 3 = 15")    labelTextYellow.set("5 x 4 = 20")    labelTextPink.set("5 x 5 = 25")    labelTextPurple.set("5 x 6 = 30")    labelTextGray.set("5 x 7 = 35")    labelTextOrange.set("5 x 8 = 40")    labelTextBlack.set("5 x 9 = 45")    labelTextBrown.set("5 x 10 = 50")def quitApp():    quit()root = Tk()mainMenu = Menu(root)root.config(menu = mainMenu)subMenu1 = Menu(mainMenu, tearoff = 0)mainMenu.add_cascade(label = "File", menu = subMenu1)subMenu1.add_command(label = "open")subMenu1.add_command(label = "Quit", command = quitApp)subMenu2 = Menu(mainMenu, tearoff = 0)mainMenu.add_cascade(label = "Choose number", menu = subMenu2)subMenu2.add_command(label = "for five", command = tableFive)entry = Entry(root)entry.grid(row = 0, column = 0, ipadx = 10, ipady = 10, padx = 10, pady = 10)showTableButton = ttk.Button(root, text = "Show Table", command = tableCount)showTableButton.grid(row = 0, column = 1, ipadx = 50, ipady = 10, padx = 10, pady = 10)labelTextRed = IntVar()labelTextRed.set("=====")labelRed = Label(root, textvariable = labelTextRed)labelRed.config(bg = "red")labelRed.grid(row = 1, column = 0, ipadx = 10, ipady = 10, padx = 10, pady = 10, sticky = W+E)labelTextBlue = IntVar()labelTextBlue.set("=====")labelBlue = Label(root, textvariable = labelTextBlue)labelBlue.config(bg = "blue")labelBlue.grid(row = 1, column = 1, ipadx = 10, ipady = 10, padx = 10, pady = 10, sticky = W+E)labelTextGreen = IntVar()labelTextGreen.set("=====")labelGreen = Label(root, textvariable = labelTextGreen)labelGreen.config(bg = "green")labelGreen.grid(row = 2, column = 0, ipadx = 10, ipady = 10, padx = 10, pady = 10, sticky = W+E)labelTextYellow = IntVar()labelTextYellow.set("=====")labelYellow = Label(root, textvariable = labelTextYellow)labelYellow.config(bg = "yellow")labelYellow.grid(row = 2, column = 1, ipadx = 10, ipady = 10, padx = 10, pady = 10, sticky = W+E)labelTextPink = IntVar()labelTextPink.set("=====")labelPink = Label(root, textvariable = labelTextPink)labelPink.config(bg = "Pink")labelPink.grid(row = 3, column = 0, ipadx = 10, ipady = 10, padx = 10, pady = 10, sticky = W+E)labelTextPurple = IntVar()labelTextPurple.set("=====")labelPurple = Label(root, textvariable = labelTextPurple)labelPurple.config(bg = "Purple")labelPurple.grid(row = 3, column = 1, ipadx = 10, ipady = 10, padx = 10, pady = 10, sticky = W+E)labelTextGray = IntVar()labelTextGray.set("=====")labelGray = Label(root, textvariable = labelTextGray)labelGray.config(bg = "Gray")labelGray.grid(row = 4, column = 0, ipadx = 10, ipady = 10, padx = 10, pady = 10, sticky = W+E)labelTextOrange = IntVar()labelTextOrange.set("=====")labelOrange = Label(root, textvariable = labelTextOrange)labelOrange.config(bg = "Orange")labelOrange.grid(row = 4, column = 1, ipadx = 10, ipady = 10, padx = 10, pady = 10, sticky = W+E)labelTextBlack = IntVar()labelTextBlack.set("=====")labelBlack = Label(root, textvariable = labelTextBlack)labelBlack.config(bg = "Black", fg = "White")labelBlack.grid(row = 5, column = 0, ipadx = 10, ipady = 10, padx = 10, pady = 10, sticky = W+E)labelTextBrown = IntVar()labelTextBrown.set("=====")labelBrown = Label(root, textvariable = labelTextBrown)labelBrown.config(bg = "Brown")labelBrown.grid(row = 5, column = 1, ipadx = 10, ipady = 10, padx = 10, pady = 10, sticky = W+E)root.mainloop()